
def solution(input):
    print(input)
    for i in range(0, len(input)-3):
        lastChars = input[i:i+4]
        if len(set(lastChars)) == len(lastChars):
            return i+4

def solution_2(input):
    print(input)
    for i in range(0, len(input)-13):
        lastChars = input[i:i+14]
        if len(set(lastChars)) == len(lastChars):
            return i+14

if __name__ == '__main__':

    solution = solution("")
    print(solution)
    print('----------------------------------------------------------\n'
          '----------------------------------------------------------')
    solution_2 = solution_2("")
    print(solution_2)