
def solution(input):
    print(input)
    for i in range(0, len(input)-3):
        lastChars = input[i:i+4]
        if len(set(lastChars)) == len(lastChars):
            return i+4


if __name__ == '__main__':

    solution = solution("")
    print(solution)
    print('----------------------------------------------------------\n'
          '----------------------------------------------------------')